const clamav = require('clamav.js');
const path = require('path');
const fs = require('fs');

// Vérifie que le fichier a été passé en argument
const filePath = process.argv[2];

if (!filePath || !fs.existsSync(filePath)) {
  console.error('Fichier non trouvé');
  process.exit(1); // Si aucun fichier n'est trouvé, on arrête le script
}

// Crée un scanner pour se connecter au serveur ClamAV local (par défaut sur le port 3310)
const scanner = clamav.createScanner(3310, 'localhost');

// Fonction pour scanner le fichier
scanner.scan(filePath, (err, object, virus) => {
  if (err) {
    console.log('Erreur:', err);
    return;
  }
  if (virus) {
    console.log(`Virus trouvé dans ${filePath} : ${virus}`);
    process.exit(1); // Le fichier contient un virus, on arrête le script avec un code d'erreur
  } else {
    console.log(`Aucun virus trouvé dans ${filePath}`);
    process.exit(0); // Aucun virus trouvé, tout va bien
  }
});
